INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`, `is_read`, `icon`) VALUES
(1, 'wc-refund-returns-page', 'info', 'en_US', 'Configura una página de política de devoluciones y reembolsos para aumentar la credibilidad de tu tienda.', 'Hemos creado un borrador de muestra de la página de política de devoluciones y reembolsos. Echa un vistazo y modifícala según las necesidades de tu tienda.', '{}', 'unactioned', 'woocommerce-core', '2024-10-22 01:48:15', NULL, 0, 'plain', '', 0, 0, 'info'),
(2, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', '¿Elegimos un tema?', 'Echa un vistazo a los temas compatibles con WooCommerce y elige uno alineado con tu marca y las necesidades de tu negocio.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-27 13:43:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(3, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Cómo personalizar tu catálogo de productos', 'Quieres que tu catálogo de productos y tus imágenes tengan un aspecto estupendo y estén en consonancia con tu marca. Esta guía te dará todos los consejos que necesitas para que tus productos se vean bien en tu tienda.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-27 13:43:57', NULL, 0, 'plain', '', 0, 0, 'info'),
(4, 'wc-admin-launch-checklist', 'info', 'en_US', '¿Estás preparado para lanzar tu tienda?', 'Para asegurarnos de que nunca tengas esa sensación de \"¿Qué se me olvidó?\" hemos preparado la lista de comprobación esencial de despegue.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-27 13:43:57', NULL, 0, 'plain', '', 0, 0, 'info'),
(5, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Protege tu tienda de WooCommerce con Jetpack Backup.', 'Tener una tienda inactiva implica una pérdida de ventas. Las restauraciones en un solo clic te ayudan a volver a estar operativo rápidamente si hay algún problema.', '{}', 'unactioned', 'woocommerce-admin-notes', '2024-10-27 13:43:57', NULL, 0, 'thumbnail', 'http://localhost/magiacali_orig/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info'),
(6, 'wc-admin-mobile-app', 'info', 'en_US', 'Instala la aplicación móvil WooCommerce', 'Instala la aplicación móvil de WooCommerce para gestionar pedidos, recibir avisos de ventas y ver métricas clave – allí donde estés.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-27 13:43:57', NULL, 0, 'plain', '', 0, 0, 'info'),
(7, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', '¡Empieza a aceptar pagos en tu tienda!', 'Acepta pagos mediante el proveedor adecuado para ti - elige entre más de 100 pasarelas de pago para WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-27 13:43:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(8, 'wayflyer_bnpl_q4_2021', 'marketing', 'en_US', 'Grow your business with funding through Wayflyer', 'Fast, flexible financing to boost cash flow and help your business grow – one fee, no interest rates, penalties, equity, or personal guarantees. Based on your store’s performance, Wayflyer provides funding and analytical insights to invest in your business.', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(9, 'wc_shipping_mobile_app_usps_q4_2021', 'marketing', 'en_US', 'Print and manage your shipping labels with WooCommerce Shipping and the WooCommerce Mobile App', 'Save time by printing, purchasing, refunding, and tracking shipping labels generated by <a href=\"https://woocommerce.com/woocommerce-shipping/\">WooCommerce Shipping</a> – all directly from your mobile device!', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(10, 'your-first-product', 'info', 'en_US', 'Your first product', 'That’s huge! You’re well on your way to building a successful online store — now it’s time to think about how you’ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(11, 'wc-admin-optimizing-the-checkout-flow', 'info', 'en_US', 'Optimizing the checkout flow', 'It’s crucial to get your store’s checkout as smooth as possible to avoid losing sales. Let’s take a look at how you can optimize the checkout experience for your shoppers.', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(12, 'wc-payments-qualitative-feedback', 'info', 'en_US', 'WooPayments setup - let us know what you think', 'Congrats on enabling WooPayments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(13, 'share-your-feedback-on-paypal', 'info', 'en_US', 'Share your feedback on PayPal', 'Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(14, 'google_listings_and_ads_install', 'marketing', 'en_US', 'Drive traffic and sales with Google', 'Reach online shoppers to drive traffic and sales for your store by showcasing products across Google, for free or with ads.', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(15, 'wc-subscriptions-security-update-3-0-15', 'info', 'en_US', 'WooCommerce Subscriptions security update!', 'We recently released an important security update to WooCommerce Subscriptions. To ensure your site’s data is protected, please upgrade <strong>WooCommerce Subscriptions to version 3.0.15</strong> or later.<br /><br />Click the button below to view and update to the latest Subscriptions version, or log in to <a href=\"https://woocommerce.com/my-dashboard\">WooCommerce.com Dashboard</a> and navigate to your <strong>Downloads</strong> page.<br /><br />We recommend always using the latest version of WooCommerce Subscriptions, and other software running on your site, to ensure maximum security.<br /><br />If you have any questions we are here to help — just <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">open a ticket</a>.', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(16, 'woocommerce-core-update-5-4-0', 'info', 'en_US', 'Update to WooCommerce 5.4.1 now', 'WooCommerce 5.4.1 addresses a checkout issue discovered in WooCommerce 5.4. We recommend upgrading to WooCommerce 5.4.1 as soon as possible.', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(17, 'wcpay-promo-2020-11', 'marketing', 'en_US', 'wcpay-promo-2020-11', 'wcpay-promo-2020-11', '{}', 'pending', 'woocommerce.com', '2024-10-27 13:44:07', NULL, 0, 'plain', '', 0, 0, 'info'),
